mapper.select:
select
/*vm.id, vm.VMName, vm.application, vm.systemName, vm.RAM, vm.memory, vm.CPU, vm.number, vm.applicant as user.id, vm.section,
vm.startDate, vm.endDate, vm.increaseTheTime, vm.username, vm.password, vm.remark, vm.adminId
from vm_application_info as vm
left join user as user
on vm.applicant = user.id*/

from vm_order_info


    and VMName = #{vmname,jdbcType=VARCHAR}


    and application = #{application,jdbcType=VARCHAR}


    and systemName = #{systemname,jdbcType=VARCHAR}


    and RAM = #{ram,jdbcType=INTEGER}


    and memory = #{memory,jdbcType=INTEGER}


    and CPU = #{cpu,jdbcType=INTEGER}


    and number = #{number,jdbcType=INTEGER}


    and applicant = #{applicant,jdbcType=VARCHAR}


    and section = #{section,jdbcType=VARCHAR}


    and startDate = #{startdate,jdbcType=TIMESTAMP}


    and endDate = #{enddate,jdbcType=TIMESTAMP}


    and increaseTheTime = #{increasethetime,jdbcType=INTEGER}


    and username = #{username,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and remark = #{remark,jdbcType=VARCHAR}


    and adminId = #{adminid,jdbcType=INTEGER}


    and environment = #{environment,jdbcType=VARCHAR}


    and applicantId = #{applicantid,jdbcType=INTEGER}


    and admin = #{admin,jdbcType=VARCHAR}


    and ip = #{ip,jdbcType=VARCHAR}


    and state = #{state,jdbcType=VARCHAR}


order by startDate desc
limit #{pagelimit},#{pagesize}

select count(id)
from vm_order_info


    and VMName = #{vmname,jdbcType=VARCHAR}


    and application = #{application,jdbcType=VARCHAR}


    and systemName = #{systemname,jdbcType=VARCHAR}


    and RAM = #{ram,jdbcType=INTEGER}


    and memory = #{memory,jdbcType=INTEGER}


    and CPU = #{cpu,jdbcType=INTEGER}


    and number = #{number,jdbcType=INTEGER}


    and applicant = #{applicant,jdbcType=VARCHAR}


    and section = #{section,jdbcType=VARCHAR}


    and startDate = #{startdate,jdbcType=TIMESTAMP}


    and endDate = #{enddate,jdbcType=TIMESTAMP}


    and increaseTheTime = #{increasethetime,jdbcType=INTEGER}


    and username = #{username,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and remark = #{remark,jdbcType=VARCHAR}


    and adminId = #{adminid,jdbcType=INTEGER}


    and environment = #{environment,jdbcType=VARCHAR}


    and applicantId = #{applicantid,jdbcType=INTEGER}


    and admin = #{admin,jdbcType=VARCHAR}


    and ip = #{ip,jdbcType=VARCHAR}


    and state = #{state,jdbcType=VARCHAR}


order by startDate desc

mapper.update:
update vm_order_info
set endDate = #{enddate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
